parameters.inputSchema:
{
  "name": "MCPToolResponse",
  "strict": false,
  "schema": {
    "type": "object",
    "additionalProperties": true,
    "required": ["toolName", "responseType"],
    "properties": {
      "toolName": {
        "type": "string",
        "description": "Name of the MCP tool that generated this response (e.g., 'discord_list_channels', 'discord_send_message', 'mcp_list_tools')"
      },
      "responseType": {
        "type": "string",
        "enum": ["list", "single", "action", "tool_catalog", "error"],
        "description": "Type of response: 'list' for arrays of items, 'single' for single objects, 'action' for operations (send/edit/delete), 'tool_catalog' for tool listings, 'error' for failures"
      },
      "data": {
        "description": "Main response data - structure varies by responseType. For 'list': array of items. For 'single': object. For 'action': operation result. For 'tool_catalog': array of tool definitions.",
        "oneOf": [
          {
            "type": "array",
            "description": "Array of items for list responses"
          },
          {
            "type": "object",
            "description": "Single object for single-item or action responses",
            "additionalProperties": true
          }
        ]
      },
      "pagination": {
        "type": ["object", "null"],
        "description": "Pagination metadata (only present for list responses that support pagination)",
        "additionalProperties": true,
        "properties": {
          "offset": {
            "type": ["number", "null"],
            "description": "Starting position for pagination",
            "minimum": 0
          },
          "limit": {
            "type": ["number", "null"],
            "description": "Number of items per page",
            "minimum": 1
          },
          "hasMore": {
            "type": ["boolean", "null"],
            "description": "Whether more items can be fetched"
          },
          "nextOffset": {
            "type": ["number", "string", "null"],
            "description": "Offset or cursor for next page if hasMore is true"
          },
          "before": {
            "type": ["string", "null"],
            "description": "Cursor/before ID for pagination (message/channel IDs, etc.)"
          },
          "after": {
            "type": ["string", "null"],
            "description": "Cursor/after ID for pagination"
          }
        }
      },
      "summary": {
        "type": ["object", "null"],
        "description": "Summary/metadata about the response (total counts, returned counts, etc.)",
        "additionalProperties": true,
        "properties": {
          "total": {
            "type": ["number", "null"],
            "description": "Total number of items available"
          },
          "returned": {
            "type": ["number", "null"],
            "description": "Number of items included in this response"
          },
          "hasMore": {
            "type": ["boolean", "null"],
            "description": "Whether more items can be fetched"
          }
        }
      },
      "status": {
        "type": ["string", "null"],
        "description": "Status for action responses: 'success', 'error', 'partial', etc.",
        "enum": ["success", "error", "partial", null]
      },
      "message": {
        "type": ["string", "null"],
        "description": "Human-readable message describing the result or error"
      },
      "error": {
        "type": ["object", "null"],
        "description": "Error details if responseType is 'error' or status is 'error'",
        "additionalProperties": true,
        "properties": {
          "code": {
            "type": ["string", "number", "null"],
            "description": "Error code if available"
          },
          "message": {
            "type": ["string", "null"],
            "description": "Error message"
          },
          "details": {
            "type": ["object", "string", "null"],
            "description": "Additional error details",
            "additionalProperties": true
          }
        }
      },
      "metadata": {
        "type": ["object", "null"],
        "description": "Additional metadata specific to the tool or response",
        "additionalProperties": true
      }
    }
  }
}
