parameters.inputSchema:
{
  "name": "ContactsDigest",
  "strict": true,
  "schema": {
    "type": "object",
    "additionalProperties": false,
    "required": ["summary", "pagination", "contacts"],
    "properties": {
      "summary": {
        "type": "object",
        "additionalProperties": false,
        "required": ["total", "returned", "hasMore"],
        "properties": {
          "total": {
            "type": "integer",
            "description": "Total number of contacts available"
          },
          "returned": {
            "type": "integer",
            "description": "Number of contacts included in this response"
          },
          "hasMore": {
            "type": "boolean",
            "description": "Whether more contacts can be fetched with a larger offset"
          }
        }
      },
      "pagination": {
        "type": "object",
        "additionalProperties": false,
        "required": ["offset", "limit"],
        "properties": {
          "offset": {
            "type": "integer",
            "minimum": 0
          },
          "limit": {
            "type": "integer",
            "minimum": 1
          },
          "nextOffset": {
            "type": ["integer", "null"],
            "description": "Offset to request next if hasMore is true, otherwise null"
          },
          "type": {
            "type": "string",
            "description": "Original list type if provided",
            "examples": ["all", "dm", "friend"]
          },
          "filtersApplied": {
            "type": "boolean",
            "description": "Whether source indicated filters are applied"
          }
        }
      },
      "contacts": {
        "type": "array",
        "items": {
          "type": "object",
          "additionalProperties": false,
          "required": ["id", "name", "username", "type", "isBot"],
          "properties": {
            "id": {
              "type": "string",
              "description": "Contact identifier as string"
            },
            "name": {
              "type": "string",
              "description": "Display name best available"
            },
            "username": {
              "type": "string",
              "description": "Handle or unique username"
            },
            "type": {
              "type": "string",
              "enum": ["dm", "friend", "other"],
              "description": "Relationship or thread type"
            },
            "isBot": {
              "type": "boolean",
              "description": "True if contact is a bot"
            },
            "status": {
              "type": "string",
              "description": "Online status if provided",
              "examples": ["online", "offline", "idle", "dnd", "unknown"]
            },
            "lastMessageAtIso": {
              "type": "string",
              "format": "date-time",
              "description": "Last message timestamp in ISO 8601 UTC if available"
            },
            "lastMessageAgo": {
              "type": "string",
              "description": "Human relative time if provided",
              "examples": ["7 days ago"]
            },
            "avatar": {
              "type": "string",
              "format": "uri",
              "description": "Avatar URL if available"
            }
          }
        }
      }
    }
  }
}
