// Discord Platform Configuration
const DISCORD_CONFIG = {
  platform: "DISCORD",
  mcpCredentialId: "ZFofx3k2ze1wsifx",
  cacheKeyPrefix: "discord",
  defaultTTL: 6 * 60 * 60 * 1000, // 6 hours

  entities: {
    "contact": {
      cacheKey: "discordContacts",
      fetchWorkflowId: "DiscordContactFetch",
      mcpTool: {
        name: "discord_list_contacts",
        parameters: { limit: 100 },
        mcpDataKey: "contacts"
      },
      mapper: "(c) => ({ id: c.id, username: c.tag, displayName: c.displayName, lastMessage: c.lastMessageAtRelative, contactType: c.type, platform: 'DISCORD' })",
      dataKey: "contacts",
      searchType: "rag",
      ragConfig: {
        collectionId: "paragon-os-contacts",
        autoIngest: true
      },
      requiresRAGStatusCheck: true
    },
    "guild": {
      cacheKey: "discordGuilds",
      fetchWorkflowId: "DiscordGuildFetch",
      mcpTool: {
        name: "discord_list_guilds",
        parameters: {},
        mcpDataKey: "guilds"
      },
      mapper: "(g) => ({ id: g.id, name: g.name, members: g.memberCount, channels: g.channels ?? [] })",
      dataKey: "guilds",
      searchType: "fuzzy",
      fuzzyConfig: {
        searchKeys: ["name", "channels"],
        matchQuality: 50,
        limit: 10
      }
    },
    "tool": {
      cacheKey: "discordTools",
      fetchWorkflowId: "DiscordToolFetch",
      mapper: "(t) => t",
      dataKey: "tools",
      searchType: "fuzzy",
      fuzzyConfig: {
        searchKeys: ["name", "description"],
        matchQuality: 50,
        limit: 10
      }
    },
    "self": {
      cacheKey: "discordProfile",
      fetchWorkflowId: "DiscordProfileFetch",
      mapper: "(p) => p",
      dataKey: "myProfile",
      searchType: "none",
      skipQuery: true
    }
  }
};

const input = $input.first().json;

return {
  query: input.query || '',
  entity: input.entity || '',
  platformConfig: DISCORD_CONFIG
};
