const input = $input.first().json;

// Tools are returned differently - they're in result.content[0].text as a JSON object
// Validate cache result - check if tools exist and is valid
if (!input || !input.tools || (Array.isArray(input.tools) && input.tools.length === 0) || (typeof input.tools === 'object' && Object.keys(input.tools).length === 0)) {
  // Try to extract from MCP response
  try {
    if (input.result?.content?.[0]?.text) {
      const parsed = JSON.parse(input.result.content[0].text);
      const tools = parsed.tools || parsed;
      if (tools && Object.keys(tools).length > 0) {
        return {
          output: {
            tools: Object.values(tools)
          }
        };
      }
    }
  } catch (e) {
    // Fall through to error
  }

  // Invalid/empty cache - return error to trigger fetch
  return {
    error: 'Invalid or empty cache result',
    shouldFetch: true,
    output: { tools: [] }
  };
}

// If tools is already an array, return as-is
if (Array.isArray(input.tools)) {
  return {
    output: {
      tools: input.tools
    }
  };
}

// If tools is an object, convert to array
return {
  output: {
    tools: Object.values(input.tools)
  }
};
