const { platform, entityType, params } = $input.first().json;

// Normalize RAG entity types (contact-rag -> contact, message-rag -> message)
const normalizedEntity = entityType.replace('-rag', '');

const configs = {
  DISCORD: {
    contact: {
      operation: 'executeTool',
      toolName: 'discord_list_contacts',
      toolParams: { limit: 100 },
      dataKey: 'contacts',
      mcpDataKey: 'contacts',
      mapper: "(c) => ({ id: c.id, username: c.tag, displayName: c.displayName, lastMessage: c.lastMessageAtRelative, contactType: c.type, platform: 'DISCORD' })",
      errorMsg: 'No valid contacts found'
    },
    guild: {
      operation: 'executeTool',
      toolName: 'discord_list_guilds',
      toolParams: {},
      dataKey: 'guilds',
      mcpDataKey: 'guilds',
      mapper: "(g) => ({ id: g.id, name: g.name, members: g.memberCount, channels: g.channels ?? [] })",
      errorMsg: 'No valid guilds found'
    },
    self: {
      operation: 'executeTool',
      toolName: 'discord_get_user_info',
      toolParams: {},
      normalizeType: 'profile'
    },
    tool: {
      operation: 'listTools',
      normalizeType: 'tools'
    }
  },
  TELEGRAM: {
    contact: {
      operation: 'executeTool',
      toolName: 'telegram_list_contacts',
      toolParams: { limit: 200 },
      dataKey: 'contacts',
      mcpDataKey: 'contacts',
      mapper: "(c) => ({ id: c.id, username: c.username, displayName: c.name, lastMessage: c.lastMessageAtRelative, contactType: c.type, phone: c.phone, platform: 'TELEGRAM' })",
      errorMsg: 'No valid contacts found'
    },
    chat: {
      operation: 'executeTool',
      toolName: 'telegram_list_chats',
      toolParams: { limit: 50 },
      dataKey: 'channels',
      mcpDataKey: 'items',
      mapper: "(g) => ({ id: g.id, name: g.name, members: g.memberCount, kind: g.kind, unread: g.unread })",
      errorMsg: 'No valid chats found'
    },
    self: {
      operation: 'executeTool',
      toolName: 'telegram_get_me',
      toolParams: {},
      normalizeType: 'profile'
    },
    tool: {
      operation: 'listTools',
      normalizeType: 'tools'
    },
    message: {
      operation: 'executeTool',
      toolName: 'telegram_list_messages',
      toolParams: {
        chat_id: params?.chat_id || '4000551178',
        limit: params?.limit || 100
      },
      dataKey: 'messages',
      mcpDataKey: '',
      mapper: "(m) => ({ id: m.id, chatId: m.chatId, text: m.text || '', senderId: m.senderId, senderName: m.senderName || m.from?.name || '', date: m.date || m.timestamp, kind: m.kind || 'message' })",
      errorMsg: 'No valid messages found'
    }
  }
};

const platformConfig = configs[platform];
if (!platformConfig) {
  throw new Error(`Unknown platform: ${platform}`);
}

const entityConfig = platformConfig[normalizedEntity];
if (!entityConfig) {
  throw new Error(`Unknown entity type: ${normalizedEntity} for platform: ${platform}`);
}

return {
  platform,
  entityType: normalizedEntity,
  config: entityConfig
};

const mcpResponse = $input.first().json;
const config = $('Build Config').first().json.config;

// Handle profile normalization (single object)
if (config.normalizeType === 'profile') {
  let profile = null;
  try {
    if (mcpResponse.result?.content?.[0]?.text) {
      profile = JSON.parse(mcpResponse.result.content[0].text);
    } else if (mcpResponse.myProfile) {
      profile = mcpResponse.myProfile;
    } else {
      throw new Error('No profile found');
    }
    return { myProfile: profile };
  } catch (error) {
    return { myProfile: null, error: error.message };
  }
}

// Handle tools normalization (object or array)
if (config.normalizeType === 'tools') {
  let tools = [];
  try {
    if (mcpResponse.result?.content?.[0]?.text) {
      const parsed = JSON.parse(mcpResponse.result.content[0].text);
      tools = parsed.tools || parsed;
    } else if (mcpResponse.tools) {
      tools = mcpResponse.tools;
    }

    // Ensure tools is an array
    if (!Array.isArray(tools)) {
      tools = Object.values(tools);
    }

    return {
      output: { tools }
    };
  } catch (error) {
    return {
      error: error.message,
      output: { tools: [] }
    };
  }
}

// Standard MCP Data Normalizer logic (contacts, guilds, chats, messages)
const { dataKey, mcpDataKey, mapper, errorMsg } = config;
let extractedData = [];

try {
  // Extract data from MCP response
  if (mcpResponse.result?.content?.[0]?.text) {
    const parsed = JSON.parse(mcpResponse.result.content[0].text);
    extractedData = mcpDataKey ? (parsed[mcpDataKey] || []) : (Array.isArray(parsed) ? parsed : []);
  } else if (mcpResponse[dataKey]) {
    extractedData = mcpResponse[dataKey];
  }

  if (!Array.isArray(extractedData) || extractedData.length === 0) {
    return { [dataKey]: [], error: errorMsg };
  }

  // Apply mapper function
  const mapperFunc = new Function('item', `return (${mapper})(item)`);
  return { [dataKey]: extractedData.map(mapperFunc) };
} catch (error) {
  return { [dataKey]: [], error: error.message };
}
