const input = $input.first().json;
const { rawData, dataKey, mcpDataKey, mapper, errorMsg } = input;

// Generic helper function
function simplifyMCPData(input, dataKey, mcpDataKey, mapper, errorMsg) {
  let extractedData = [];
  try {
    // Handle different MCP response formats
    if (input.result?.content?.[0]) {
      const parsed = JSON.parse(input.result.content[0].text);
      extractedData = parsed?.[mcpDataKey] || [];
    } else if (input[dataKey]) {
      extractedData = Array.isArray(input[dataKey]) ? input[dataKey] : [];
    } else if (input.output?.[dataKey]) {
      extractedData = Array.isArray(input.output[dataKey]) ? input.output[dataKey] : [];
    } else if (Array.isArray(input)) {
      // Handle case where input is directly an array
      extractedData = input;
    } else if (mcpDataKey && input[mcpDataKey]) {
      extractedData = Array.isArray(input[mcpDataKey]) ? input[mcpDataKey] : [];
    }

    if (!Array.isArray(extractedData) || extractedData.length === 0) {
      return { [dataKey]: [], error: errorMsg, shouldFetch: true };
    }

    // Execute mapper function (stored as string - e.g., "(item) => ({ id: item.id })")
    let mapperFunc;
    try {
      // Try to evaluate mapper as a function
      mapperFunc = new Function('item', `return (${mapper})(item)`);
    } catch (e) {
      // If that fails, try as a direct expression
      mapperFunc = new Function('item', `return ${mapper}`);
    }

    return { [dataKey]: extractedData.map(mapperFunc) };
  } catch (error) {
    return { [dataKey]: [], error: error.message, shouldFetch: true };
  }
}

return simplifyMCPData(rawData, dataKey, mcpDataKey, mapper, errorMsg);
