// ╔══════════════════════════════════════════════════════════════════╗
// ║                    LOOKUP LOGIC (don't edit)                      ║
// ╚══════════════════════════════════════════════════════════════════╝

const fs = require('fs');
const path = require('path');

// Load test cases from external file
// Try multiple possible paths where test-cases.js might be located
const possiblePaths = [
  path.join(process.cwd(), 'test-cases.js'),
  path.join(process.cwd(), 'n8n-agent', 'test-cases.js'),
  path.resolve(__dirname || '.', '../../test-cases.js'),
  path.resolve(__dirname || '.', '../../../n8n-agent/test-cases.js'),
  '/Users/nipuna/Software/paragon-os/paragon-os-app/n8n-agent/test-cases.js'
];

let TESTS;
let loadedPath;

for (const testCasesPath of possiblePaths) {
  try {
    if (fs.existsSync(testCasesPath)) {
      // Try require first (cleaner)
      try {
        delete require.cache[require.resolve(testCasesPath)];
        TESTS = require(testCasesPath);
        loadedPath = testCasesPath;
        break;
      } catch (requireError) {
        // Fallback: read and eval
        const testCasesContent = fs.readFileSync(testCasesPath, 'utf8');
        // Extract TESTS object from the file content
        const match = testCasesContent.match(/const TESTS = ({[\s\S]*?});/);
        if (match) {
          TESTS = eval('(' + match[1] + ')');
          loadedPath = testCasesPath;
          break;
        }
      }
    }
  } catch (error) {
    // Continue to next path
    continue;
  }
}

if (!TESTS) {
  return {
    error: 'Failed to load test cases from any of the following paths:',
    attemptedPaths: possiblePaths,
    cwd: process.cwd(),
    __dirname: __dirname
  };
}

const { workflow, testCase } = $input.first().json;

// List all available tests if no specific one requested
if (!workflow || !testCase) {
  const available = {};
  for (const [wf, cases] of Object.entries(TESTS)) {
    available[wf] = Object.keys(cases);
  }
  return {
    error: 'Please provide workflow and testCase parameters',
    available,
    loadedFrom: loadedPath
  };
}

// Get the specific test case
const workflowTests = TESTS[workflow];
if (!workflowTests) {
  return {
    error: `Workflow '${workflow}' not found`,
    availableWorkflows: Object.keys(TESTS)
  };
}

const data = workflowTests[testCase];
if (!data) {
  return {
    error: `Test case '${testCase}' not found in '${workflow}'`,
    availableTestCases: Object.keys(workflowTests)
  };
}

// Return the test data
return data;

