// ╔══════════════════════════════════════════════════════════════════╗
// ║                    TEST CASES DATABASE                            ║
// ║  Add your test cases here. They sync with workflow backups.       ║
// ╚══════════════════════════════════════════════════════════════════╝

const TESTS = {

  // ────────────────────────────────────────────────────────────────
  // Telegram Context Scout
  // ────────────────────────────────────────────────────────────────
  'TelegramContextScout': {
    'contact-rag': {
      query: 'sebastian',
      entity: 'contact-rag'
    },
    'contact-fuzzy': {
      query: 'lanka',
      entity: 'contact'
    },
    'chat-search': {
      query: 'metarune',
      entity: 'chat'
    },
    'tool-lookup': {
      query: 'send message',
      entity: 'tool'
    },
    'self-profile': {
      query: '',
      entity: 'self'
    }
  },

  // ────────────────────────────────────────────────────────────────
  // Dynamic RAG
  // ────────────────────────────────────────────────────────────────
  'DynamicRAG': {
    'status': {
      mode: 'STATUS',
      collectionId: 'paragon-os-contacts'
    },
    'search-contacts': {
      mode: 'SEARCH',
      collectionId: 'paragon-os-contacts',
      input: 'lanka'
    },
    'search-metarune': {
      mode: 'SEARCH',
      collectionId: 'chat-agent-experiment-1',
      input: 'metarune'
    },
    'create-collection': {
      mode: 'CREATE',
      collectionId: 'test-collection'
    },
    'delete-collection': {
      mode: 'DELETE',
      collectionId: 'test-collection'
    },
    'clear-collection': {
      mode: 'CLEAR',
      collectionId: 'test-collection'
    }
  },

  // ────────────────────────────────────────────────────────────────
  // Discord Context Scout (template - add your tests)
  // ────────────────────────────────────────────────────────────────
  'DiscordContextScout': {
    'example': {
      query: 'example query',
      entity: 'contact'
    }
  }

};

// ╔══════════════════════════════════════════════════════════════════╗
// ║                    LOOKUP LOGIC (don't edit)                      ║
// ╚══════════════════════════════════════════════════════════════════╝

const { workflow, testCase } = $input.first().json;

// List all available tests if no specific one requested
if (!workflow || !testCase) {
  const available = {};
  for (const [wf, cases] of Object.entries(TESTS)) {
    available[wf] = Object.keys(cases);
  }
  return {
    error: 'Please provide workflow and testCase parameters',
    available
  };
}

// Get the specific test case
const workflowTests = TESTS[workflow];
if (!workflowTests) {
  return {
    error: `Workflow '${workflow}' not found`,
    availableWorkflows: Object.keys(TESTS)
  };
}

const data = workflowTests[testCase];
if (!data) {
  return {
    error: `Test case '${testCase}' not found in '${workflow}'`,
    availableTestCases: Object.keys(workflowTests)
  };
}

// Return the test data
return data;

