// ╔══════════════════════════════════════════════════════════════════╗
// ║                    LOOKUP LOGIC (don't edit)                      ║
// ╚══════════════════════════════════════════════════════════════════╝

// Test data is passed directly from the CLI via testData parameter
// This avoids file system access issues in n8n's Code node

const input = $input.first().json;
const { workflow, testCase, testData } = input;

// If testData is provided directly, return it
if (testData && typeof testData === 'object' && Object.keys(testData).length > 0) {
  return testData;
}

// Otherwise, return an error
return {
  error: 'Test data not provided. The CLI should pass testData directly.',
  workflow,
  testCase,
  received: { workflow, testCase, hasTestData: !!testData },
  hint: 'Make sure the test CLI passes testData in the Test Config node'
};

