{
  "tools": [
    {
      "name": "discord_read_channel",
      "description": "Read messages from a Discord channel or direct message (DM) chat using IDs only. Provide either a channelId to read any channel, or a userId to read the DM channel with that user. Supports comprehensive pagination options.",
      "schema": {
        "type": "object",
        "properties": {
          "channelId": {
            "type": "string",
            "description": "The Discord channel ID to read messages from (alternative to userId)."
          },
          "userId": {
            "type": "string",
            "description": "The Discord user ID to read DM chat history with (alternative to channelId). Use this to list direct messages with a specific user."
          },
          "limit": {
            "type": "number",
            "description": "Number of messages to fetch (default: 50, max: 100)",
            "default": 50
          },
          "before": {
            "type": "string",
            "description": "Message ID to fetch messages before (for pagination)"
          },
          "after": {
            "type": "string",
            "description": "Message ID to fetch messages after (for pagination)"
          },
          "beforeDate": {
            "type": "string",
            "description": "ISO date string to fetch messages before (e.g., \"2023-12-01T10:00:00Z\")"
          },
          "afterDate": {
            "type": "string",
            "description": "ISO date string to fetch messages after (e.g., \"2023-12-01T10:00:00Z\")"
          },
          "applyFilters": {
            "type": "boolean",
            "description": "Whether to apply channel whitelist/blacklist filters (default: false)",
            "default": false
          }
        },
        "required": []
      }
    },
    {
      "name": "discord_search_messages",
      "description": "Search for messages in a Discord channel by content, author, or date range",
      "schema": {
        "type": "object",
        "properties": {
          "channelId": {
            "type": "string",
            "description": "The Discord channel ID to search messages in"
          },
          "query": {
            "type": "string",
            "description": "Text to search for in message content"
          },
          "authorId": {
            "type": "string",
            "description": "Optional: Filter by author ID"
          },
          "limit": {
            "type": "number",
            "description": "Number of messages to search through (default: 100, max: 500)",
            "default": 100
          },
          "before": {
            "type": "string",
            "description": "Optional: Search messages before this message ID"
          },
          "after": {
            "type": "string",
            "description": "Optional: Search messages after this message ID"
          }
        },
        "required": [
          "channelId"
        ]
      }
    },
    {
      "name": "discord_send_message",
      "description": "Send a message to a specific Discord channel or user. Can accept either a channel ID or user ID (will create DM channel automatically). Supports file attachments.",
      "schema": {
        "type": "object",
        "properties": {
          "channelId": {
            "type": "string",
            "description": "The Discord channel ID or user ID to send the message to"
          },
          "content": {
            "type": "string",
            "description": "The message content to send"
          },
          "replyToMessageId": {
            "type": "string",
            "description": "Optional: Message ID to reply to"
          },
          "filePaths": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Optional: Array of local file paths to attach (max 10 files)"
          },
          "maxFileSizeMB": {
            "type": "number",
            "description": "Optional: Maximum file size in MB (default: 10MB, Discord limit for non-Nitro users)"
          }
        },
        "required": [
          "channelId",
          "content"
        ]
      }
    },
    {
      "name": "discord_edit_message",
      "description": "Edit a previously sent Discord message. Can only edit your own messages. Accepts channel ID or user ID.",
      "schema": {
        "type": "object",
        "properties": {
          "channelId": {
            "type": "string",
            "description": "The Discord channel ID or user ID where the message was sent"
          },
          "messageId": {
            "type": "string",
            "description": "The ID of the message to edit"
          },
          "content": {
            "type": "string",
            "description": "The new message content"
          }
        },
        "required": [
          "channelId",
          "messageId",
          "content"
        ]
      }
    },
    {
      "name": "discord_reply_message",
      "description": "Reply to a specific Discord message. Creates a message that references the original message. Accepts channel ID or user ID. Supports file attachments.",
      "schema": {
        "type": "object",
        "properties": {
          "channelId": {
            "type": "string",
            "description": "The Discord channel ID or user ID where the message is located"
          },
          "messageId": {
            "type": "string",
            "description": "The ID of the message to reply to"
          },
          "content": {
            "type": "string",
            "description": "Your reply message content"
          },
          "filePaths": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Optional: Array of local file paths to attach (max 10 files)"
          },
          "maxFileSizeMB": {
            "type": "number",
            "description": "Optional: Maximum file size in MB (default: 10MB, Discord limit for non-Nitro users)"
          }
        },
        "required": [
          "channelId",
          "messageId",
          "content"
        ]
      }
    },
    {
      "name": "discord_delete_message",
      "description": "Delete a Discord message. For your own messages: always deletes from Discord. For others' messages with force=true: tries Discord deletion first, falls back to hiding the message locally if Discord API restricts it (e.g., in DMs).",
      "schema": {
        "type": "object",
        "properties": {
          "channelId": {
            "type": "string",
            "description": "The Discord channel ID or user ID where the message is located"
          },
          "messageId": {
            "type": "string",
            "description": "The ID of the message to delete"
          },
          "force": {
            "type": "boolean",
            "description": "Bypass ownership check and attempt to delete any message. For others' messages: tries Discord deletion first, falls back to hiding locally if restricted. Default: false",
            "default": false
          }
        },
        "required": [
          "channelId",
          "messageId"
        ]
      }
    },
    {
      "name": "discord_download_attachments",
      "description": "Download all attachments (images, videos, files, etc.) from a specific Discord message to a local directory. Supports all attachment types.",
      "schema": {
        "type": "object",
        "properties": {
          "channelId": {
            "type": "string",
            "description": "The Discord channel ID containing the message"
          },
          "messageId": {
            "type": "string",
            "description": "The Discord message ID with attachments to download"
          },
          "downloadPath": {
            "type": "string",
            "description": "Optional: Custom download directory path (default: ~/.discord-mcp-server/downloads/)"
          },
          "maxFileSizeMB": {
            "type": "number",
            "description": "Optional: Maximum file size in MB (default: 50MB). Files larger than this will be skipped."
          }
        },
        "required": [
          "channelId",
          "messageId"
        ]
      }
    },
    {
      "name": "discord_list_guilds",
      "description": "List all Discord servers (guilds) the user is in",
      "schema": {
        "type": "object",
        "properties": {}
      }
    },
    {
      "name": "discord_list_channels",
      "description": "List all accessible channels for the current user with pagination support",
      "schema": {
        "type": "object",
        "properties": {
          "guildId": {
            "type": "string",
            "description": "Optional: Filter channels by Discord server (guild) ID"
          },
          "limit": {
            "type": "number",
            "description": "Number of channels to fetch per page (default: 50, max: 500)",
            "default": 50
          },
          "offset": {
            "type": "number",
            "description": "Starting position for pagination (default: 0)",
            "default": 0
          },
          "applyFilters": {
            "type": "boolean",
            "description": "Whether to apply channel whitelist/blacklist filters (default: true)",
            "default": true
          },
          "checkPermissions": {
            "type": "boolean",
            "description": "Whether to filter channels based on user permissions (default: true)",
            "default": true
          }
        }
      }
    },
    {
      "name": "discord_list_guild_members",
      "description": "List members of a specific Discord server (guild)",
      "schema": {
        "type": "object",
        "properties": {
          "guildId": {
            "type": "string",
            "description": "The Discord server (guild) ID to list members from"
          },
          "limit": {
            "type": "number",
            "description": "Number of members to fetch (default: 50, max: 1000)",
            "default": 50
          },
          "includeRoles": {
            "type": "boolean",
            "description": "Whether to include role information for each member",
            "default": false
          }
        },
        "required": [
          "guildId"
        ]
      }
    },
    {
      "name": "discord_list_channel_filters",
      "description": "List whitelist and blacklist filters for Discord servers (guilds), Direct Messages (DMs), or group DMs. Use type=\"guild\" for server channels, type=\"dm\" for individual user DMs, type=\"group\" for group DM channels, type=\"guilds\" for filtering guilds themselves.",
      "schema": {
        "type": "object",
        "properties": {
          "type": {
            "type": "string",
            "enum": [
              "guild",
              "dm",
              "group",
              "guilds"
            ],
            "description": "Type of filters to list: \"guild\" (Discord server channels), \"dm\" (Direct Messages with specific users), \"group\" (Group DM channels), or \"guilds\" (filtering guilds themselves)"
          },
          "guildId": {
            "type": "string",
            "description": "The Discord server (guild) ID - REQUIRED only when type=\"guild\""
          }
        },
        "required": [
          "type"
        ]
      }
    },
    {
      "name": "discord_update_channel_filters",
      "description": "Update whitelist and blacklist filters for Discord servers (guilds), Direct Messages (DMs), or group DMs. Use type=\"guild\" for server channels, type=\"dm\" for individual user DMs, type=\"group\" for group DM channels, type=\"guilds\" for filtering guilds themselves. Supports multi-item operations.",
      "schema": {
        "type": "object",
        "properties": {
          "type": {
            "type": "string",
            "enum": [
              "guild",
              "dm",
              "group",
              "guilds"
            ],
            "description": "Type of filters to update: \"guild\" (Discord server channels), \"dm\" (Direct Messages with specific users), \"group\" (Group DM channels), or \"guilds\" (filtering guilds themselves)"
          },
          "guildId": {
            "type": "string",
            "description": "The Discord server (guild) ID - REQUIRED only when type=\"guild\""
          },
          "operation": {
            "type": "string",
            "enum": [
              "add_whitelist",
              "remove_whitelist",
              "add_blacklist",
              "remove_blacklist",
              "set_whitelist",
              "set_blacklist",
              "clear"
            ],
            "description": "The operation to perform: add_whitelist, remove_whitelist, add_blacklist, remove_blacklist, set_whitelist, set_blacklist, or clear"
          },
          "channelIds": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Array of channel IDs - USE for type=\"guild\" (server channels) and type=\"group\" (group DM channels). NOT used for type=\"dm\" or type=\"guilds\""
          },
          "userIds": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Array of user IDs - USE ONLY for type=\"dm\" (Direct Messages with specific users). NOT used for type=\"guild\", type=\"group\", or type=\"guilds\""
          },
          "guildIds": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Array of guild IDs - USE ONLY for type=\"guilds\" (filtering guilds themselves). NOT used for type=\"guild\", type=\"dm\", or type=\"group\""
          }
        },
        "required": [
          "type",
          "operation"
        ]
      }
    },
    {
      "name": "discord_get_pending_guild_channels",
      "description": "Get pending channels within a Discord server (guild) that require attention. Returns channels with either unread messages or @mentions. Only works for text channels within a single guild/server. Helps you identify channels that need follow-up.",
      "schema": {
        "type": "object",
        "properties": {
          "guildId": {
            "type": "string",
            "description": "Discord server (guild) ID to get pending channels from"
          },
          "limit": {
            "type": "number",
            "description": "Number of pending channels to fetch per page (default: 50, max: 200)",
            "default": 50
          },
          "offset": {
            "type": "number",
            "description": "Starting position for pagination (default: 0)",
            "default": 0
          },
          "includeUnread": {
            "type": "boolean",
            "description": "Include channels with unread messages (default: true)",
            "default": true
          },
          "includeMentions": {
            "type": "boolean",
            "description": "Include channels with @mentions (default: true)",
            "default": true
          },
          "applyFilters": {
            "type": "boolean",
            "description": "Whether to apply channel whitelist/blacklist filters (default: true)",
            "default": true
          }
        },
        "required": [
          "guildId"
        ]
      }
    },
    {
      "name": "discord_get_user_info",
      "description": "Get information about the logged-in user",
      "schema": {
        "type": "object",
        "properties": {}
      }
    },
    {
      "name": "discord_list_contacts",
      "description": "List all Discord contacts (DMs and friends) with pagination. By default, applies DM whitelist/blacklist filters to only show allowed DM contacts. Use this to browse or retrieve complete contact lists. For searching by name, use search_contacts instead.",
      "schema": {
        "type": "object",
        "properties": {
          "limit": {
            "type": "number",
            "description": "Number of contacts to fetch per page (default: 20, max: 200)",
            "default": 20
          },
          "offset": {
            "type": "number",
            "description": "Starting position for pagination (default: 0)",
            "default": 0
          },
          "type": {
            "type": "string",
            "description": "Filter contacts by type: \"dm\" for direct messages, \"friend\" for friends, \"all\" for both (default: \"all\")",
            "enum": [
              "dm",
              "friend",
              "all"
            ],
            "default": "all"
          },
          "userIds": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Optional: List of user IDs to fetch contact details for (bypasses pagination)"
          },
          "fetchFresh": {
            "type": "boolean",
            "description": "Whether to fetch fresh data from API instead of cache (default: false)",
            "default": false
          },
          "applyFilters": {
            "type": "boolean",
            "description": "Whether to apply DM whitelist/blacklist filters (default: true)",
            "default": true
          }
        }
      }
    },
    {
      "name": "discord_search_contacts",
      "description": "Search for specific Discord contacts by name with partial/fuzzy matching. Use this when looking for a particular person (e.g., \"find John\", \"search for brian\"). Supports typos and partial matches. If no results found in cache, automatically tries fetchFresh to search fresh data.",
      "schema": {
        "type": "object",
        "properties": {
          "query": {
            "type": "string",
            "description": "Search term to match against usernames, display names, and tags"
          },
          "fetchFresh": {
            "type": "boolean",
            "description": "Whether to fetch fresh data from API instead of cache (default: false)",
            "default": false
          },
          "limit": {
            "type": "number",
            "description": "Maximum number of results to return (default: 20, max: 100)",
            "default": 20
          },
          "includeMatchScore": {
            "type": "boolean",
            "description": "Whether to include detailed match scoring information (default: false)",
            "default": false
          }
        },
        "required": [
          "query"
        ]
      }
    },
    {
      "name": "discord_get_pending_dms",
      "description": "Get pending Direct Messages (DMs) that require attention. Returns DMs with either unread messages or DMs you opened but haven't replied to yet. Only works for 1-on-1 DM conversations, not guild/server channels. Helps you identify personal conversations that need follow-up.",
      "schema": {
        "type": "object",
        "properties": {
          "limit": {
            "type": "number",
            "description": "Number of pending DMs to fetch per page (default: 20, max: 200)",
            "default": 20
          },
          "offset": {
            "type": "number",
            "description": "Starting position for pagination (default: 0)",
            "default": 0
          },
          "includeUnread": {
            "type": "boolean",
            "description": "Include DMs with unread messages (default: true)",
            "default": true
          },
          "includeOpened": {
            "type": "boolean",
            "description": "Include DMs you opened but haven't replied to (default: true)",
            "default": true
          },
          "applyFilters": {
            "type": "boolean",
            "description": "Whether to apply DM whitelist/blacklist filters (default: true)",
            "default": true
          }
        }
      }
    }
  ]
}
