// Parse responses into { title, prompt, caption, hashtags[], hashtags_string }
return $input.all().map(item => {
  const data = item.json || {};

  let title = "";
  let prompt = "";
  let caption = "";
  let hashtagsArr = [];

  // 1) NOUVEAU FORMAT : { output: { title, prompt, caption, hashtags } }
  if (data.output) {
    const o = data.output || {};
    title = o.title ?? "";
    prompt = o.prompt ?? "";
    caption = o.caption ?? "";

    const rawHashtags = o.hashtags ?? [];
    if (Array.isArray(rawHashtags)) {
      hashtagsArr = rawHashtags;
    } else if (typeof rawHashtags === "string") {
      try {
        const parsedTags = JSON.parse(rawHashtags);
        if (Array.isArray(parsedTags)) {
          hashtagsArr = parsedTags;
        } else {
          hashtagsArr = rawHashtags.split(/[,\s]+/).filter(Boolean);
        }
      } catch {
        hashtagsArr = rawHashtags.split(/[,\s]+/).filter(Boolean);
      }
    }
  }

  // 2) ANCIEN FORMAT OpenAI (fallback)
  if (!title && !prompt && !caption && hashtagsArr.length === 0) {
    let content =
      data?.choices?.[0]?.message?.content ??
      data?.message?.content ??
      data?.content ??
      null;

    if (content && typeof content === "object") {
      title = content.title ?? "";
      prompt = content.prompt ?? "";
      caption = content.caption ?? "";

      if (Array.isArray(content.hashtags)) {
        hashtagsArr = content.hashtags;
      } else if (typeof content.hashtags === "string") {
        hashtagsArr = content.hashtags.split(/[,\s]+/).filter(Boolean);
      }
    }
    else if (typeof content === "string" && content.trim()) {
      try {
        const parsed = JSON.parse(content);
        title = parsed.title ?? "";
        prompt = parsed.prompt ?? "";
        caption = parsed.caption ?? "";

        if (Array.isArray(parsed.hashtags)) {
          hashtagsArr = parsed.hashtags;
        } else if (typeof parsed.hashtags === "string") {
          hashtagsArr = parsed.hashtags.split(/[,\s]+/).filter(Boolean);
        }
      } catch {
        title = "";
        prompt = "";
        caption = "";
        hashtagsArr = [];
      }
    }
  }

  // 3) Normalisation des hashtags
  const norm = Array.from(
    new Set(
      (hashtagsArr || [])
        .map(h => (h ?? "").toString().trim())
        .filter(Boolean)
        .map(h => (h.startsWith("#") ? h : `#${h}`))
    )
  );

  const hashtags_string = norm.join(" ");

  return {
    json: {
      title,
      prompt,
      caption,
      hashtags: norm,
      hashtags_string
    }
  };
});

