// Telegram Platform Configuration
const TELEGRAM_CONFIG = {
  platform: "TELEGRAM",
  mcpCredentialId: "aiYCclLDUqob5iQ0",
  cacheKeyPrefix: "telegram",
  defaultTTL: 6 * 60 * 60 * 1000, // 6 hours

  entities: {
    "contact": {
      cacheKey: "telegramContacts",
      fetchWorkflowId: "TelegramContactFetch",
      mcpTool: {
        name: "telegram_list_contacts",
        parameters: { limit: 200 },
        mcpDataKey: "contacts"
      },
      mapper: "(c) => ({ id: c.id, username: c.username, displayName: c.name, lastMessage: c.lastMessageAtRelative, contactType: c.type, phone: c.phone })",
      dataKey: "contacts",
      searchType: "rag",
      ragConfig: {
        collectionId: "paragon-os-contacts",
        autoIngest: true
      },
      requiresRAGStatusCheck: true
    },
    "contact-rag": {
      cacheKey: "telegramContacts",
      fetchWorkflowId: "TelegramContactFetch",
      mcpTool: {
        name: "telegram_list_contacts",
        parameters: { limit: 200 },
        mcpDataKey: "contacts"
      },
      mapper: "(c) => ({ id: c.id, username: c.username, displayName: c.name, lastMessage: c.lastMessageAtRelative, contactType: c.type, phone: c.phone })",
      dataKey: "contacts",
      searchType: "rag",
      ragConfig: {
        collectionId: "paragon-os-contacts",
        autoIngest: true
      },
      requiresRAGStatusCheck: true
    },
    "chat": {
      cacheKey: "telegramChannels",
      fetchWorkflowId: "TelegramChatFetch",
      mcpTool: {
        name: "telegram_list_chats",
        parameters: { limit: 50 },
        mcpDataKey: "items"
      },
      mapper: "(g) => ({ id: g.id, name: g.name, members: g.memberCount, kind: g.kind, unread: g.unread })",
      dataKey: "channels",
      searchType: "fuzzy",
      fuzzyConfig: {
        searchKeys: ["name", "kind"],
        matchQuality: 50,
        limit: 10
      }
    },
    "tool": {
      cacheKey: "telegramTools",
      fetchWorkflowId: "TelegramToolFetch",
      mapper: "(t) => t",
      dataKey: "tools",
      searchType: "fuzzy",
      fuzzyConfig: {
        searchKeys: ["name", "description"],
        matchQuality: 50,
        limit: 10
      }
    },
    "self": {
      cacheKey: "telegramProfile",
      fetchWorkflowId: "TelegramProfileFetch",
      mapper: "(p) => p",
      dataKey: "myProfile",
      searchType: "none",
      skipQuery: true
    },
    "message-rag": {
      cacheKey: "telegramMessages",
      fetchWorkflowId: "TelegramMessageFetch",
      mcpTool: {
        name: "telegram_list_messages",
        parameters: { chat_id: "4000551178", limit: 100 },
        mcpDataKey: ""
      },
      mapper: "(m) => ({ id: m.id, chatId: m.chatId, text: m.text || '', senderId: m.senderId, senderName: m.senderName || m.from?.name || '', date: m.date || m.timestamp, kind: m.kind || 'message' })",
      dataKey: "messages",
      searchType: "rag",
      ragConfig: {
        collectionId: "paragon-os-messages",
        autoIngest: true
      },
      requiresRAGStatusCheck: true
    }
  }
};

const input = $input.first().json;

// Debug: Log input to see what we're receiving
if (!input.entity) {
  throw new Error(`Entity is missing from input. Received: ${JSON.stringify(input)}`);
}

return {
  query: input.query || '',
  entity: input.entity,
  platformConfig: TELEGRAM_CONFIG
};

// Explicitly structure data for executeWorkflow
const input = $input.first().json;

// Ensure entity is present
if (!input.entity || input.entity === '') {
  throw new Error(`Entity is missing or empty. Input: ${JSON.stringify(input)}`);
}

// Return structured data
return {
  query: input.query || '',
  entity: input.entity,
  platformConfig: input.platformConfig
};
