const input = $input.first().json;

// Generic helper function (inline)
function simplifyMCPData(input, dataKey, mcpDataKey, mapper, errorMsg) {
  let rawData = [];
  try {
    if (input.result?.content?.[0]) {
      const parsed = JSON.parse(input.result.content[0].text);
      rawData = parsed?.[mcpDataKey] || [];
    } else if (input[dataKey]) {
      rawData = Array.isArray(input[dataKey]) ? input[dataKey] : [];
    } else if (input.output?.[dataKey]) {
      rawData = Array.isArray(input.output[dataKey]) ? input.output[dataKey] : [];
    }

    if (!Array.isArray(rawData) || rawData.length === 0) {
      return { [dataKey]: [], error: errorMsg, shouldFetch: true };
    }

    return { [dataKey]: rawData.map(mapper) };
  } catch (error) {
    return { [dataKey]: [], error: error.message, shouldFetch: true };
  }
}

// Use it
return simplifyMCPData(
  input,
  'contacts',
  'contacts',
  (c) => ({
    id: c.id,
    username: c.username,
    displayName: c.name,
    lastMessage: c.lastMessageAtRelative,
    contactType: c.type,
    phone: c.phone
  }),
  'No valid contacts found'
);

