// Transform RAG search results to match expected format
const input = $input.first().json;

// RAG returns results in format: { results: [...] }
// Each result has: { content: { contacts: [...] }, score: ... }
// We need to extract contacts and flatten to telegramContacts array

let telegramContacts = [];

try {
  if (input.results && Array.isArray(input.results)) {
    // Extract contacts from each RAG result
    for (const result of input.results) {
      if (result.content && result.content.contacts && Array.isArray(result.content.contacts)) {
        telegramContacts.push(...result.content.contacts);
      }
    }
  } else if (input.contacts && Array.isArray(input.contacts)) {
    // Fallback: if contacts are directly in the response
    telegramContacts = input.contacts;
  } else if (Array.isArray(input)) {
    // Fallback: if the response is directly an array
    telegramContacts = input;
  }
} catch (error) {
  // If transformation fails, return empty array
  telegramContacts = [];
}

// Return in the format expected by FuzzySearch
return {
  telegramContacts: telegramContacts
};

