const input = $input.first().json;

let telegramMessages = [];

try {
  if (input.results && Array.isArray(input.results)) {
    for (const result of input.results) {
      if (result.content && result.content.messages && Array.isArray(result.content.messages)) {
        telegramMessages.push(...result.content.messages);
      }
    }
  } else if (input.messages && Array.isArray(input.messages)) {
    telegramMessages = input.messages;
  } else if (Array.isArray(input)) {
    telegramMessages = input;
  }
} catch (error) {
  telegramMessages = [];
}

return {
  telegramMessages: telegramMessages
};

const triggerData = $('When Executed by Another Workflow').first().json;
const mode = triggerData.mode || 'SEARCH';
const query = triggerData.query || '';
const testMode = triggerData.testMode || false;

// For INSERT mode, wrap query in proper structure
// For SEARCH mode, use query as-is
const input = mode === 'INSERT' 
  ? { content: { text: query }, metadata: {} }
  : query;

// Use -dev collections for tests, production collections otherwise
const collectionId = testMode ? 'paragon-os-knowledge-dev' : 'paragon-os-knowledge';

return {
  mode: mode,
  collectionId: collectionId,
  input: input
};
