parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "executionSteps": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "toolName": {
            "type": "string",
            "description": "The exact MCP tool name to execute (e.g., 'telegram_list_messages', 'telegram_send_message')"
          },
          "callParams": {
            "type": "object",
            "description": "The parameters object for the tool call with all required IDs and values",
            "additionalProperties": true
          },
          "reasoning": {
            "type": "string",
            "description": "Brief explanation of why this step is needed"
          }
        },
        "required": ["toolName", "callParams"],
        "additionalProperties": false
      },
      "description": "Execution step array to achieve the user's goal"
    },
    "chatsUsed": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string"
          },
          "name": {
            "type": "string"
          },
          "kind": {
            "type": "string",
            "description": "Telegram chat kind, e.g. 'user', 'group', 'supergroup', 'channel'"
          },
          "unread": {
            "type": "boolean",
            "description": "Whether this chat currently has unread messages"
          }
        }
      },
      "description": "List of chat objects that were referenced when providing the answer to user prompt"
    },
    "contactsUsed": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string"
          },
          "username": {
            "type": "string"
          },
          "displayName": {
            "type": "string"
          },
          "phone": {
            "type": "string"
          }
        }
      },
      "description": "List of Telegram contacts that were referenced when providing the answer to user prompt"
    },
    "toolsUsed": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string"
          },
          "description": {
            "type": "string"
          },
          "inputSchema": {
            "type": "object"
          }
        }
      },
      "description": "List of MCP tool specifications used in the executionStep execution"
    },
    "answerToUserPrompt": {
      "type": "string",
      "description": "Your answer to the user prompt after calling Telegram Context Scout & executing Telegram MCP calls for executionSteps. MUST be proper markdown formatted."
    }
  },
  "required": [
    "executionSteps",
    "chatsUsed",
    "contactsUsed",
    "toolsUsed",
    "answerToUserPrompt"
  ],
  "additionalProperties": false
}
